function FirebaseHelper(){function _getConfig(){var config;return FirebaseConfig().getConfig()}function init(){var config=_getConfig();firebase.initializeApp(config)}function getUser(){return firebase.auth().currentUser}function getReference(name){return firebase.database().ref(name)}function signIn(email,password){return firebase.auth().signInWithEmailAndPassword(email,password)}function signOut(){return firebase.auth().signOut()}return{init:init,getUser:getUser,getReference:getReference,signIn:signIn,signOut:signOut}}function FirebasePageReference(){var _ref=null;function init(){_ref=firebase.database().ref("pages")}function getReference(){return _ref}function add(page){var newRef=_ref.push(),newItem={id:newRef.key,title:page.title,text:page.text,date:page.date,user:page.user};return newRef.set(newItem),newRef.key}function save(pageKey,newValues){_ref.child(pageKey).update({title:newValues.title,text:newValues.text,date:newValues.date,user:newValues.user})}function remove(pageKey){_ref.child(pageKey).remove()}return{init:init,getReference:getReference,add:add,save:save,remove:remove}}